async function seedDorarHadiths() {
  console.log("Starting Dorar/Mawsua hadith seeding...");
  
  let inserted = 0;
  let skipped = 0;
  
  for (const hadith of dorarHadiths) {
    try {
      const existing = await db.query.verificationHadiths.findFirst({
        where: (h, { eq }) => eq(h.text, hadith.text)
      });
      
      if (!existing) {
        await db.insert(verificationHadiths).values(hadith);
        inserted++;
      } else {
        skipped++;
      }
    } catch (error) {
      console.error("Error inserting hadith:", hadith.text.substring(0, 50), error);
    }
  }
  
  console.log(`\nSeeding complete!`);
  console.log(`Inserted: ${inserted}`);
  console.log(`Skipped (duplicates): ${skipped}`);
  console.log(`Total hadiths in batch: ${dorarHadiths.length}`);
  
  const counts = await db.execute(`
    SELECT status, COUNT(*) as count 
    FROM verification_hadiths 
    GROUP BY status
    ORDER BY count DESC
  `);
  console.log("\nCurrent grade distribution:");
  for (const row of counts.rows as any[]) {
    console.log(`  ${row.status}: ${row.count}`);
  }
  
  const total = await db.execute(`SELECT COUNT(*) as total FROM verification_hadiths`);
  console.log(`\nTotal hadiths in database: ${(total.rows[0] as any).total}`);
  
  process.exit(0);
}

seedDorarHadiths();
